generate_insert_constraint(A, B, C) :-
        make_name('$insert_in_store_', B, D),
        E=..[D,F],
        generate_insert_constraint_body(A, B, F, G),
        C=(E:-G).
generate_insert_constraint_body(default, A, B, C) :-
        get_target_module(_),
